create database fb_users;
use fb_users;

CREATE TABLE Users(
	userid INT primary key,
    age INT,
    dob_day INT,
    dob_year INT,
    dob_month INT,
    gender enum ('female','male'),
    tendure INT
    );
    


	
	CREATE TABLE actividad(
	userid INT PRIMARY KEY,
    friend_count INT,
    friendships_initiated INT,
    likes INT,
    likes_recerived INT,
    FOREIGN KEY (userid) REFERENCES Users(userid)
    );
    
